<?phpinclude_once('db_conf.php');class M_Db{    private $db;    public static $Instance = NULL;    public static function getInstance()    {        if (self::$Instance == NULL)        {            self::$Instance = new M_Db();        }        return self::$Instance;    }    private function __construct()    {        $database = "test6";        $host = "127.0.0.1";        $user = "loot";        $password = "";        $this->db =  new  PDO('mysql:dbname='.$database.';host='.$host.'',$user,$password, array(PDO::MYSQL_ATTR_INIT_COMMAND => "SET NAMES utf8"));    }    public function prepare($sql)    {        $sth = $this->db->prepare($sql, $values = null);        $sth->execute($values);        return $sth;    }}